import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './WorksheetForm.css';

// Base URL for API requests
const API_BASE_URL = 'https://sravya1.pythonanywhere.com/api';

function WorksheetForm() {
  // State for form inputs
  const [worksheetType, setWorksheetType] = useState('spiral');
  const [difficulty, setDifficulty] = useState('beginner');
  const [selectedConcepts, setSelectedConcepts] = useState([]);
  const [includeAnswerKey, setIncludeAnswerKey] = useState(false);
  const [questionCount, setQuestionCount] = useState(15);

  // State for API data
  const [availableConcepts, setAvailableConcepts] = useState([]);
  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState(null);
  const [pdfUrl, setPdfUrl] = useState(null);
  const [successMessage, setSuccessMessage] = useState('');

  // Fetch available concepts on component mount
  useEffect(() => {
    const fetchConcepts = async () => {
      try {
        const response = await axios.get(`${API_BASE_URL}/concepts`);
        setAvailableConcepts(response.data.concepts);
      } catch (err) {
        console.error('Error fetching concepts:', err);
        setError('Failed to load math concepts. Please try again later.');
      }
    };

    fetchConcepts();
  }, []);

  // Handle concept selection
  const handleConceptChange = (conceptId) => {
    // In fluency mode, only allow one concept
    if (worksheetType === 'fluency') {
      setSelectedConcepts([conceptId]);
      return;
    }

    // In spiral mode, toggle the concept
    if (selectedConcepts.includes(conceptId)) {
      setSelectedConcepts(selectedConcepts.filter(id => id !== conceptId));
    } else {
      setSelectedConcepts([...selectedConcepts, conceptId]);
    }
  };

  // Handle worksheet type change
  const handleWorksheetTypeChange = (type) => {
    setWorksheetType(type);
    // If switching to fluency, limit to one concept
    if (type === 'fluency' && selectedConcepts.length > 1) {
      setSelectedConcepts([selectedConcepts[0]]);
    }
  };

  // Handle form submission
  const handleSubmit = async (e) => {
    e.preventDefault();

    // Validation
    if (selectedConcepts.length === 0) {
      setError('Please select at least one math concept.');
      return;
    }

    setIsLoading(true);
    setError(null);
    setPdfUrl(null);
    setSuccessMessage('');

    try {
      const response = await axios.post(`${API_BASE_URL}/generate-worksheet`, {
        worksheet_type: worksheetType,
        difficulty,
        concepts: selectedConcepts,
        include_answer_key: includeAnswerKey,
        question_count: questionCount
      });

      // Set download URL for the PDF
      setPdfUrl(`${API_BASE_URL}/download/${response.data.file_path.split('/').pop()}`);
      setSuccessMessage('Worksheet generated successfully!');
    } catch (err) {
      console.error('Error generating worksheet:', err);
      setError(err.response?.data?.detail || 'Failed to generate worksheet. Please try again.');
    } finally {
      setIsLoading(false);
    }
  };

  // Handle select/deselect all concepts
  const handleSelectAll = () => {
    if (worksheetType === 'fluency') {
      setError('Fluency practice only allows one concept. Please select individually.');
      return;
    }

    setSelectedConcepts(availableConcepts.map(concept => concept.id));
  };

  const handleDeselectAll = () => {
    setSelectedConcepts([]);
  };

  return (
    <div className="worksheet-form-container">
      <div className="form-header">
        <h1>Math Worksheet Generator <span className="k2-badge">K-2</span></h1>
        <p>Create customized math worksheets for homeschooling and education</p>
      </div>

      <form onSubmit={handleSubmit} className="worksheet-form">
        {/* Worksheet Type Selection */}
        <div className="form-section">
          <h2 className="section-title">Worksheet Type</h2>
          <div className="type-selector-container">
            <label className={`type-selector ${worksheetType === 'spiral' ? 'selected' : ''}`}>
              <input
                type="radio"
                name="worksheet_type"
                value="spiral"
                checked={worksheetType === 'spiral'}
                onChange={() => handleWorksheetTypeChange('spiral')}
              />
              <div className="selector-inner">
                <span className="selector-icon">üåÄ</span>
                <div className="selector-text">
                  <span className="selector-title">Spiral Review</span>
                  <span className="selector-desc">Multiple concepts, one problem each</span>
                </div>
              </div>
            </label>

            <label className={`type-selector ${worksheetType === 'fluency' ? 'selected' : ''}`}>
              <input
                type="radio"
                name="worksheet_type"
                value="fluency"
                checked={worksheetType === 'fluency'}
                onChange={() => handleWorksheetTypeChange('fluency')}
              />
              <div className="selector-inner">
                <span className="selector-icon">‚ö°</span>
                <div className="selector-text">
                  <span className="selector-title">Fluency Practice</span>
                  <span className="selector-desc">Single concept, timed practice</span>
                </div>
              </div>
            </label>
          </div>

          <div className="mode-info">
            <div className="mode-info-icon">‚ÑπÔ∏è</div>
            <div>
              {worksheetType === 'spiral'
                ? 'Spiral Review includes one problem from each selected concept'
                : 'Fluency Practice allows only one concept selection'}
            </div>
          </div>

          {worksheetType === 'fluency' && (
            <div className="question-count-control">
              <label htmlFor="questionCount">Number of Problems:</label>
              <input
                type="number"
                id="questionCount"
                min="5"
                max="30"
                value={questionCount}
                onChange={(e) => setQuestionCount(parseInt(e.target.value))}
              />
            </div>
          )}
        </div>

        {/* Difficulty Level */}
        <div className="form-section">
          <h2 className="section-title">Difficulty Level</h2>
          <div className="difficulty-selector">
            <label className={`difficulty-option ${difficulty === 'beginner' ? 'selected' : ''}`}>
              <input
                type="radio"
                name="difficulty"
                value="beginner"
                checked={difficulty === 'beginner'}
                onChange={() => setDifficulty('beginner')}
              />
              <div>Beginner</div>
              <div className="difficulty-desc">Single-digit numbers</div>
            </label>

            <label className={`difficulty-option ${difficulty === 'intermediate' ? 'selected' : ''}`}>
              <input
                type="radio"
                name="difficulty"
                value="intermediate"
                checked={difficulty === 'intermediate'}
                onChange={() => setDifficulty('intermediate')}
              />
              <div>Intermediate</div>
              <div className="difficulty-desc">Double-digit numbers</div>
            </label>

            <label className={`difficulty-option ${difficulty === 'advanced' ? 'selected' : ''}`}>
              <input
                type="radio"
                name="difficulty"
                value="advanced"
                checked={difficulty === 'advanced'}
                onChange={() => setDifficulty('advanced')}
              />
              <div>Advanced</div>
              <div className="difficulty-desc">Triple-digit numbers</div>
            </label>
          </div>
        </div>

        {/* Math Concepts */}
        <div className="form-section">
          <div className="section-header">
            <h2 className="section-title">Select Math Concepts</h2>
            <div className="select-buttons">
              <button type="button" onClick={handleSelectAll} className="select-btn">
                Select All
              </button>
              <button type="button" onClick={handleDeselectAll} className="select-btn">
                Deselect All
              </button>
            </div>
          </div>

          <div className="concept-grid">
            {availableConcepts.map((concept) => (
              <label
                key={concept.id}
                className={`concept-item ${selectedConcepts.includes(concept.id) ? 'selected' : ''}`}
              >
                <input
                  type="checkbox"
                  name="concepts"
                  value={concept.id}
                  checked={selectedConcepts.includes(concept.id)}
                  onChange={() => handleConceptChange(concept.id)}
                />
                <span className="concept-icon">{concept.icon}</span>
                <span>{concept.name}</span>
              </label>
            ))}
          </div>
        </div>

        {/* Answer Key Option */}
        <div className="form-section">
          <label className="answer-key-option">
            <input
              type="checkbox"
              checked={includeAnswerKey}
              onChange={() => setIncludeAnswerKey(!includeAnswerKey)}
            />
            Include Answer Key
          </label>
        </div>

        {/* Error/Success Messages */}
        {error && <div className="error-message">{error}</div>}
        {successMessage && <div className="success-message">{successMessage}</div>}

        {/* Generate Button */}
        <div className="form-actions">
          <button type="submit" className="generate-btn" disabled={isLoading}>
            {isLoading ? 'Generating...' : 'Generate Worksheet'}
          </button>
        </div>

        {/* PDF Download */}
        {pdfUrl && (
          <div className="download-section">
            <a href={pdfUrl} target="_blank" rel="noopener noreferrer" className="download-btn">
              Download PDF
            </a>
          </div>
        )}
      </form>
    </div>
  );
}

export default WorksheetForm;
